median(BMI_GLP1_BEFORE_vs_AFTER$BMI_Prior) # 37
median(BMI_GLP1_BEFORE_vs_AFTER$BMI_After) # 33.1

write.csv(BMI_GLP1_BEFORE_vs_AFTER, "BMI_GLP1_BEFORE_vs_AFTER_closest_6months.csv")



